import React, {useState} from "react";


function CreateNote() {

//Criando um state vazio que ganhará os valores passados pelo usuário
    const[note, setNote] = useState({
      title:"",
      content:""
  })

    function handleChange(event){
        //Destruturando um objeto
        const {name, value} = event.target

        setNote()
    }

  return (
    <div>
      <form>
        {/* Valores do state passados pro título e conteúdo que será digitado pelo usuário. Ambos recebem a função handleChange que atualizará os valores. */}

        <input name="title" onChance={handleChange} value={note.title} placeholder="Title" />
        <textarea name="content" onChance={handleChange}value={note.content}placeholder="Take a note..." rows="3" />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateNote;
